<?php

namespace App\Http\Controllers\homepage;

use App\Http\Controllers\Controller;
use App\Models\Page;
use Illuminate\Http\Request;
use Illuminate\Support\Facades\DB;
use App\Components\Comment;
use App\Components\System;
use Cache;

class HomeController extends Controller
{
    protected $comment;
    public function __construct()
    {
        $this->comment = new Comment();
        $this->system = new System();
    }
    public function index()
    {
        $fcSystem = $this->system->fcSystem();

        // SLIDE:
        $slideHome = getSlide('slide-home');

        // ĐỐI TÁC:
        $partners = getSlide('partner');

        // DANH MỤC SẢN PHẨM
        $productHome = Cache::remember('$productHome', 600, function () {
            $productHome = \App\Models\CategoryProduct::select('id', 'title', 'image', 'slug')
                ->where(['alanguage' => config('app.locale'), 'publish' => 0, 'ishome' => 1])
                ->orderBy('order', 'asc')
                ->orderBy('id', 'desc')
                ->get();
            if( $productHome ){
                foreach ( $productHome as $key => $val ){
                    $productHome[$key]['data'] = \App\Models\Product::select('id', 'title', 'image', 'price', 'price_sale', 'slug', 'price_contact', 'price_import', 'unit')
                        ->join('catalogues_relationships', 'products.id', '=', 'catalogues_relationships.moduleid')
                        ->where(['products.publish' => 0, 'catalogueid' => $val->id, 'module' => 'products'])
                        ->orderBy('products.order', 'asc')
                        ->with('product_versions')
                        ->orderBy('products.id', 'desc')
                        ->limit(10)
                        ->get();
                }
            }
            return $productHome;
        });

        //page: HOME
        $page = Cache::remember('pageHome', 600, function () {
            $page = Page::where(['alanguage' => config('app.locale'), 'page' => 'index', 'publish' => 0])
                ->select('id', 'title', 'image', 'meta_title', 'meta_description', 'page')
                ->with('postmetasMany')
                ->first();
            return $page;
        });

        $view = 'home';

        $seo['canonical'] = url('/');
        $seo['meta_title'] = !empty($page['meta_title']) ? $page['meta_title'] : $page['title'];
        $seo['meta_description'] = !empty($page['meta_description']) ? $page['meta_description'] : '';
        $seo['meta_image'] = !empty($page['image']) ? url($page['image']) : '';
        return view('homepage.home.index', compact('page', 'seo', 'view', 'fcSystem', 'slideHome' ,'partners', 'productHome'));
    }
    public function sitemap()
    {
        /*
        $Tags = \App\Models\Tag::select('id', 'slug', 'created_at')->where('alanguage', config('app.locale'))->where('publish', 0)->get();
        $Brands = \App\Models\Brand::select('id', 'slug', 'created_at')->where('alanguage', config('app.locale'))->where('publish', 0)->get(); */
        $router = DB::table('router')->select('slug', 'created_at')->whereIn('module',['products','category_products','articles','category_articles'])->get();
        return response()->view('homepage.home.sitemap', compact('router'))->header('Content-Type', 'text/xml');
    }
